muclient.script:
function OnPluginBroadcast (msg, id, name, text) if id == '3e7dedbe37e44942dd46d264' then if text:find("char.status") then res, gmcparg = CallPlugin("3e7dedbe37e44942dd46d264","gmcpval","char") char = assert (loadstring ('return ' .. gmcparg or ""))()
if not firstactive and tonumber(char.status.state) == 3 then firstactive = true DoAfterSpecial(1, "phelper:broadcast(1, 'firstactive', 'firstactive')", 12) end end end phelper:OnPluginBroadcast(msg, id, name, text) end
function OnPluginInstall () --OnPluginEnable is automatically called by pluginhelper
phelper:OnPluginInstall() end -- OnPluginInstall
function OnPluginClose ()
phelper:OnPluginClose() end -- OnPluginClose
function OnPluginEnable () ldplugin ("aard_GMCP_handler", "3e7dedbe37e44942dd46d264") ldplugin ("broadcast_quest")
phelper:OnPluginEnable() end -- OnPluginEnable
function OnPluginDisable ()
phelper:OnPluginDisable() end -- OnPluginDisable
function OnPluginConnect ()
phelper:OnPluginConnect() end -- function OnPluginConnect
function OnPluginDisconnect () firstactive = false
phelper:OnPluginDisconnect() end -- function OnPluginConnect
function OnPluginSaveState ()
phelper:OnPluginSaveState() end -- function OnPluginSaveState
