muclient.script:
function OnPluginBroadcast (msg, id, name, text) if id == 'aaa9e381c5408278ccda7100' then if msg == 1 then if phelper.dtrackskill ~= "" then SendNoEcho('dtrack skill ' .. phelper.dtrackskill) end if phelper.creategroup then creategroup() end end elseif id == '3e7dedbe37e44942dd46d264' then if text:find("char.base") then res, gmcparg = CallPlugin("3e7dedbe37e44942dd46d264","gmcpval","char") char = assert (loadstring ('return ' .. gmcparg or ""))() end end phelper:OnPluginBroadcast(msg, id, name, text) end
function OnPluginInstall () --OnPluginEnable is automatically called by pluginhelper
phelper:OnPluginInstall() end -- OnPluginInstall
function OnPluginClose ()
phelper:OnPluginClose() end -- OnPluginClose
function OnPluginEnable () ldplugin ("aard_GMCP_handler", "3e7dedbe37e44942dd46d264") ldplugin ("broadcast_state", "aaa9e381c5408278ccda7100")
phelper:OnPluginEnable() end -- OnPluginEnable
function OnPluginDisable ()
phelper:OnPluginDisable() end -- OnPluginDisable
function OnPluginConnect ()
phelper:OnPluginConnect() end -- function OnPluginConnect
function OnPluginDisconnect ()
phelper:OnPluginDisconnect() end -- function OnPluginConnect
function OnPluginSaveState ()
phelper:OnPluginSaveState() end -- function OnPluginSaveState
